import React, { useEffect } from 'react';
import {useLaunchMissionInfoQuery } from './../../generated/graphql'
import MissionList from './MissionInfo'
// useEffect use karo ye const {data,error,loading}
// aur variables wale section main
// taake jab tak user select na kare koi naam tab
// tak kuch show na ho

//Abb agar save karain ge 

interface Props{
    idInput:number
}

const MissionInfoContainer = ({idInput}:Props)=>{

    const {data,loading,error, refetch}=useLaunchMissionInfoQuery({
        variables:{id:idInput.toString()}
    });

    useEffect(()=>{
        refetch()
    },[refetch])

    if(loading){
        return(
            <h1>Loading...</h1>
        )
    }
    if(error || !data){
        return <h3>An Error Has Occured</h3>
    }
    console.log("data",data);
    return(
            <MissionList data={data}/>
    )
}



export default MissionInfoContainer;
